INSERT INTO permission (id, title, subtitle) VALUES (1, 'Create users', 'Allows to create user accounts'), (2, 'Modify users', 'Allows to modify user accounts'), (3, 'Delete users', 'Allows to delete user accounts'), (4, 'Send messages', 'Allows to message other user accounts'), (5, 'Read /etc/shadow', 'Allows to read hashed system passwords'), (6, 'Delete database schema', 'Allows to execute DROP SCHEMA public CASCADE'), (7, 'VPN access', 'Allows connections over VPN'), (8, 'Physical access', 'Allows connections over keyboard and monitor (enforced by drones in the server room)'), (9, 'Superuser', 'Highest-priviledge adminstrative permission level'), (10, 'Administrator', 'Administrator permission level'), (11, 'Regular user', 'Regular user permission level');
